visualEditor.code:
console.log(context);

// Extracting data from the dataset
var categoryData = context.editor.dataset.source.slice(1).map(function (item) {
  return item[0];
});

var metricsData1 = context.editor.dataset.source.slice(1).map(function (item) {
  return item[1];
});

var metricsData2 = context.editor.dataset.source.slice(1).map(function (item) {
  return item[2];
});

var metricsData3 = [];
for (var i = 0; i < metricsData1.length; i++) {
  var diff = metricsData1[i] - metricsData2[i];
  metricsData3.push(diff);
}

return {
  dataset: context.editor.dataset,
  xAxis: {
    type: 'category',
    data: categoryData
  },
  yAxis: {
    type: 'value'
  },
  series: [
    {
      type: 'bar',
      stack: 'total',
      name: "Frekeunsi Transaksi QRIS Inflow  (Ribu Transaksi)",
      data: metricsData1,
      tooltip: {
        show: true,
        trigger: 'axis',
        formatter: function (params) {
          return 'Volume Transaksi : Rp ' + params[0].value.toFixed(2) + 'Milyar';
        }
      }
    },
    {
      type: 'bar',
      stack: 'total',
      name: "Frekeunsi Transaksi QRIS Outflow  (Ribu Transaksi)",
      data: metricsData2,
      tooltip: {
        show: true,
        trigger: 'axis',
        formatter: function (params) {
          return 'Volume Transaksi : Rp ' + params[0].value.toFixed(2) + 'Milyar';
        }
      }
    },
    {
      type: 'bar',
      stack: 'total',
      name: "Selisih Transaksi QRIS (Ribu Transaksi)",
      data: metricsData3,
      tooltip: {
        show: true,
        trigger: 'axis',
        formatter: function (params) {
          return 'Volume Transaksi : Rp ' + params[0].value.toFixed(2) + 'Milyar';
        }
      }
    }
  ],
  tooltip: {
    trigger: 'axis',
    axisPointer: {
      type: 'shadow'
    }
  },
};

