console.log(context);

// Extracting data from the dataset
var categoryData = context.editor.dataset.source.slice(1).map(function (item) {
  return item[0];
});

var metricsData1 = context.editor.dataset.source.slice(1).map(function (item) {
  return item[1];
});

var metricsData2 = context.editor.dataset.source.slice(1).map(function (item) {
  return item[2];
});

var metricsData3 = [];
for (var i = 0; i < metricsData1.length; i++) {
  var diff = metricsData1[i] - metricsData2[i];
  metricsData3.push(diff);
}

option = {
  dataset: context.editor.dataset,
  xAxis: {
    type: 'category',
    data: categoryData
  },
  yAxis: {
    type: 'value'
  },
  series: [
    {
      type: 'bar',
      stack: 'total',
      name: "Volume Transaksi EDC Inflow  (Rp Milyar)",
      data: metricsData1,
      tooltip: {
        show: true,
        trigger: 'axis',
        formatter: function (params) {
          return 'Volume Transaksi : Rp ' + params[0].value.toFixed(2) + 'Milyar';
        }
      }
    },
    {
      type: 'bar',
      stack: 'total',
      name: "Volume Transaksi EDC Outflow  (Rp Milyar)",
      data: metricsData2,
      tooltip: {
        show: true,
        trigger: 'axis',
        formatter: function (params) {
          return 'Volume Transaksi : Rp ' + params[0].value.toFixed(2) + 'Milyar';
        }
      }
    },
    {
      type: 'bar',
      stack: 'total',
      name: "Selisih Transaksi EDC (Rp Milyar)",
      data: metricsData3,
      tooltip: {
        show: true,
        trigger: 'axis',
        formatter: function (params) {
          return 'Volume Transaksi : Rp ' + params[0].value.toFixed(2) + 'Milyar';
        }
      }
    }
  ],
  tooltip: {
    trigger: 'axis',
    axisPointer: {
      type: 'shadow'
    }
  },
};

return option;

options.getOption:
const series = data.series.map((s) => {
  const sData = s.fields.find((f) => f.type === 'metrics_value').values.buffer || s.fields.find((f) => f.type === 'metrics_value').values;
  const sTime = s.fields.find((f) => f.type === 'trx_date').values.buffer || s.fields.find((f) => f.type === 'trx_date').values;

  return {
    name: s.refId,
    type: 'line',
    showSymbol: false,
    areaStyle: {
      opacity: 0.1,
    },
    lineStyle: {
      width: 1,
    },
    data: sData.map((d, i) => [sTime[i], d.toFixed(2)]),
  };
});

/**
 * Enable Data Zoom by default
 */
setTimeout(() => echartsInstance.dispatchAction({
  type: 'takeGlobalCursor',
  key: 'dataZoomSelect',
  dataZoomSelectActive: true,
}), 500);

/**
 * Update Time Range on Zoom
 */
echartsInstance.on('datazoom', function (params) {
  const startValue = params.batch[0]?.startValue;
  const endValue = params.batch[0]?.endValue;
  locationService.partial({ from: startValue, to: endValue });
});

return {
  backgroundColor: 'transparent',
  tooltip: {
    trigger: 'axis',
  },
  legend: {
    left: '0',
    bottom: '0',
    data: data.series.map((s) => s.refId),
    textStyle: {
      color: 'rgba(128, 128, 128, .9)',
    },
  },
  toolbox: {
    feature: {
      dataZoom: {
        yAxisIndex: 'none',
        icon: {
          zoom: 'path://',
          back: 'path://',
        },
      },
      saveAsImage: {},
    }
  },
  xAxis: {
    type: 'time',
  },
  yAxis: {
    type: 'value',
    min: 'dataMin',
  },
  grid: {
    left: '2%',
    right: '2%',
    top: '2%',
    bottom: 24,
    containLabel: true,
  },
  series,
};

